import React, {useState, useEffect} from 'react';
import Review from './Review';
import {ReviewContainer, 
    SearchWrapper, 
    SearchBox, 
    SearchButton, 
    SearchTerm, 
    SearchIcon,
    ReviewCard,
    JobTitle,
    Company,
    Country,
    ReviewBody,
    ReviewReaction} from './ReviewSection.elements'







const ReviewSection = () => {

    const [reviews, setReviews] = useState([]);



useEffect(  () => {

     getReviews();

}, []);

const getReviews = async () => {
    const response = await fetch(
    `https://0mx7c07qka.execute-api.eu-north-1.amazonaws.com/company?q=Spotify`);
    const data = await response.json();
    setReviews(data.hits);
    console.log(data.hits);

}

    return (
        <ReviewContainer>
            <SearchWrapper>
            <SearchBox>
                <SearchTerm type="text" placeholder="Search for any company e.g Spotify" />
                    <SearchButton type="submit">
                        <SearchIcon />
                    </SearchButton>
            </SearchBox>
            </SearchWrapper>

            <ReviewCard>
                <JobTitle>
                    <h1>Title</h1>
                </JobTitle>
                <Company>
                    <h5>Spotify</h5>
                </Company>
                <Country>
                    <img src="" alt="" />
                </Country>
                <ReviewBody>
                    <p>this is my review, it's a good and honest review.</p>
                </ReviewBody>
                <ReviewReaction>
                    <img src="" alt="" />
                </ReviewReaction>

            </ReviewCard> 

        </ReviewContainer>
    )
}

export default ReviewSection;
