background-color: #f8f5f2;
padding: 1rem 0 2rem 0;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
overflow-x: hidden;


`;

export const SearchWrapper = styled.div`
   position: relative;
   justify-content: center;
    align-items: center;
    width: 50%;
    margin-top: 10px;
    padding: 0 0 2rem 0;

`;

