ThumbsUpIcon*/} from './ReviewSection.elements'








const ReviewSection = () => {

    const [reviews, setReviews] = useState([]);

    const [search, setSearch] = useState('')

    const [ query, setQuery] = useState('Spotify')



useEffect(  () => {

     getReviews();

}, []);

const getReviews = async () => {
    const response = await fetch(
    `https://0mx7c07qka.execute-api.eu-north-1.amazonaws.com/company?q=${query}`
    );
    const data = await response.json();
    setReviews(data.Items);
    console.log(data.Items);
}

const updateSearch = e => {

    setSearch(e.target.value);
    console.log(search)

}

const getSearch = e => {
    e.preventDefault();
    setQuery(search);
    setSearch('');
}

    return (
<Container>
            <SearchForm onSubmit={getSearch}>
                    <SearchBar 
                    type="text" 
                    placeholder="Search for any company e.g Spotify" 
                    value={search} 
                    onChange={updateSearch} />
                        <SearchButton type="submit">
                            <SearchIcon /* Seearch button */ />
                    </SearchButton>  
            </SearchForm>


        <ReviewContainer>
        {reviews.map(r => 
    <Review
    jobDescription={r.jobDescription}
    company={r.company}
    reviewBody={r.reviewBody}
    country={r.country}
    />
    )}

         </ReviewContainer> 

    </Container>


    )
}

export default ReviewSection;
