import styled, {createGlobalStyle} from 'styled-components'

const GlobalStyle = createGlobalStyle`
* {
box-sizing: border-box;
margin: 0;
padding: 0;
font-family: 'Source Sans Pro', sans-serif;
}
`;

export const Container = styled.div`
z-index:1;
width: 100%;
max-width: 1300px;
margin-left: auto;
margin-right:auto;
padding-left:50px;
padding-right: 50px;

@media screen and (max-width:991px){
    padding-left:50px;
    padding-right: 30px;
}
`;
export const Button = styled.button`

border-radius: 8px;
background: ${({primary}) => (primary ? '#045959' : 'transparent') };
white-space: nowrap;
padding: ${({big}) => (big ? '12px 64px' : '10px 20px') };
color: ${({primary}) => (primary ? '#fff' : '#045959') };
font-size: ${({fontBig}) => (fontBig ? '20px' : '16px') };
border: 3px solid #045959;
outline: none;
cursor: pointer;

&:hover {
    transition: all 0.1s ease-out;
    background: ${({primary}) => (primary ? '#F8F5F2' :'#045959') };
    border: ${({primary}) => (primary ? '3px solid #045959' : 'none') };
    color: ${({primary}) => (primary ? '#045959' : '#fff') };


}

@media screen and (max-width: 960px) {
    width: 100%;
}

`
export default GlobalStyle
