import styled from 'styled-components';

export const InfoSec = styled.div`

color: #045959;
padding: 160px 0;
background: ${({ lightBg }) => (lightBg ? '#F8F5F2' : '#173439')};

`;

export const InfoRow = styled.div`
display: flex;
margin: 0 -15px -15px -15px;
flex-wrap: wrap;
align-items: center;
flex-direction: row;

`;

export const InfoColumn = styled.div`
margin-bottom: 15px;
padding-right: 15px;
padding-left: 15px;
flex: 1; //tror denna bestämmer vad som visas först, i detta fall så kmr texten visas först. detta ska ändras.
max-width: 50%;
flex-basis: 50%;

@media screen and (max-width: 768) {
    max-width: 100%;
    flex-basis: 100%;
    display: flex;
    justify-content: center;


}
`;

export const TextWrapper = styled.div`
max-width: 540px;
padding-top: 0;
padding-bottom: 60px;

@media screen and (max-width: 768){
   padding-bottom: 65px;
}

`;

export const TopLine = styled.div`
color: ${({ lightTopLine }) => (lightTopLine ? '#103636' : '#3DD68D')} ;
font-size: 12px;
line-height: 16px;
letter-spacing: 1.6px;
font-weight: bold;
margin-bottom: 16px;

text-transform: uppercase;

`;

export const Heading = styled.h1`
margin-bottom: 24px;
font-size: 48px;
line-height: 1.1;
color: ${({lightText}) => (lightText ? '#045959' : '#3DD68D')};

`;

export const Subtitle = styled.p`
max-width: 440px;
margin-bottom: 35px;
font-size: 20px;
line-height: 24px;
color: ${({lightTextDesc}) => (lightTextDesc ? '#045959' : '#3dd68d' )};

`;

export const ImgWrapper = styled.div`
max-width: 555px;
display: flex;
justify-content:${({start}) => (start ? 'flex-start' : 'flex-end')};
`;

export const Img = styled.img`
padding-right: 0;
border: 0;
max-width: 100%;
vertical-align: middle;
display: inline-block;
max-height: 500px;
`;


