import React, {useState, useEffect} from 'react';
import {ReviewContainer, SearchWrapper, SearchBox, SearchButton, SearchTerm, SearchIcon} from './ReviewSection.elements'







const ReviewSection = () => {

    const [reviews, setReviews] = useState([]);


    const[query, setQuery] = useState('Spotify')


useEffect(  () => {

     getReviews();

}, [query]);

const getReviews = async () => {
    const proxyurl = `https://cors-anywhere.herokuapp.com`

    const response = await fetch(proxyurl +
    `https://0mx7c07qka.execute-api.eu-north-1.amazonaws.com/company?q=Spotify`);
    const data = await response.json();
    setReviews(data.hits);

}

    return (
        <ReviewContainer>

            <SearchWrapper>
            <SearchBox>
                <SearchTerm type="text" placeholder="Search for any company e.g Spotify" />
                    <SearchButton type="submit">
                        <SearchIcon />
                    </SearchButton>
            </SearchBox>
            </SearchWrapper>

        </ReviewContainer>
    )
}

export default ReviewSection;
