import styled from 'styled-components';

export const InfoSec = styled.div`

color: #045959;
padding: 160px 0;
background: ${({ lightBg }) => (lightBg ? '#F8F52' : '#173439')};

`;

export const InfoRow = styled.div`
display: flex;
margin: 0 -15px -15px -15px;
flex-wrap: wrap;
align-items: center;
flex-direction: row;

`;

export const InfoColumn = styled.div`
margin-bottom: 15px;
padding-right: 15px;
padding-left: 15px;
flex: 1; //tror denna bestämmer vad som visas först, i detta fall så kmr texten visas först. detta ska ändras.
max-width: 50%;
flex-basis: 50%;

@media screen and (max-width: 768) {
    max-width: 100%;
    flex-basis: 100%;
    display: flex;
    justify-content: center;


}
`;

export const TextWrapper = styled.div`
max-width: 540px;
padding-top: 0;
padding-bottom: 60px;

@media screen and (max-width: 768){
   padding-bottom: 65px;
}

`



/* Colors from figma

#F8F5F2 - BG
